 <Input type="number" placeholder="e.g., 10" {...field} />
 </FormControl>
 <FormMessage />
 </FormItem>
 )}
 />
 <FormField
 control={addStockForm.control}
 name="cost"
 render={({ field }) => (
 <FormItem>
 <FormLabel>Cost/Share</FormLabel>
 <FormControl>
 <Input type="number" placeholder="e.g., 2900.00" {...field} />
 </FormControl>
 <FormMessage />
 </FormItem>
