 <Header usageStats={usageStats}/>
 <div className="grid gap-6 md:grid-cols-5 lg:grid-cols-3">
 <div className="md:col-span-3 lg:col-span-1">
 <PortfolioForm onAnalyze={handleAnalyze} isLoading={isLoading} />
 </div>
